<style lang="scss">
	@import 'basecamp';

	.label {
		overflow-y: hidden;
		margin: 4%;
	}

	.navbar {
		background: $grabber;
		box-shadow: $mainShadow;
		backdrop-filter: blur(1.5px);
		-webkit-backdrop-filter: blur(1.5px);

		transition: $tSpeed;
		position: fixed;
		right: 0;
		overflow: hidden;
		z-index: 50;

		.home {
			display: initial;
			transition: $tSpeed;
		}

		&:hover .home {
			display: initial;
			transition: $tSpeed;
		}
	}

	.navbar-nav {
		list-style: none;
		padding: 0;
		margin: 0;
		display: flex;
		flex-direction: column;
		align-items: center;
		height: 100%;
	}

	.nav-item {
		width: 100%;
	}

	.nav-link {
		display: flex;
		align-items: center;
		height: 4rem;
		padding: $pad;
		color: $textTwo;
		font-weight: 800;
		text-decoration: none;
		filter: grayscale(80%) opacity(0.7);
		transition: 0.3s;

		&:hover {
			filter: grayscale(0%) opacity(1);
			background: $extra;
			color: $textTwo;
		}

		.nav-extra {
			position: absolute;
			top: 66px;
			right: 0;
			width: 180px;
			display: none;
			z-index: 88888;
		}

		.nav-extra li a {
			padding: 10px;
			background: #fff;
			display: none;
		}

		svg {
			width: 2.25rem;
			margin: 5%;
			appearance: auto;
		}

		.rotat {
			transform: rotate(90deg);
		}
	}

	.link-text {
		display: none;
		margin-left: 1rem;
		font-size: 2vw;
		padding: 1%;
	}

	.fa-primary {
		color: $backTre;
	}

	.fa-secondary {
		color: $extra;
	}

	.fa-primary,
	.fa-secondary {
		transition: $tSpeed;
	}

	.logo {
		font-weight: bold;
		text-transform: uppercase;
		margin-bottom: 1rem;
		text-align: center;
		color: $textMain;
		background: $extra;
		font-size: 1rem;
		letter-spacing: 0.3ch;
		width: 100%;

		&:hover svg {
			transform: rotate(180deg);
			transition: $tSpeed;
		}
	}

	.logo-text {
		font-style: italic;
