<style lang="scss">
	@import '../src/lib/basecamp';

	body {
		overflow-x: hidden;
	}

	//entry banner

	.art-banner {
		height: 100vh;
		display: grid;
		background: $backMain;
		grid-template-columns: 20px 1fr 1fr 1fr 20px;
		grid-template-rows: 20px 1fr 1fr 1fr 20px;
		margin: 5px 0;
		z-index: 99;

		a {
			font-size: 3vw;
		}
	}

	.art-banner-bg {
		background-image: url(/img/LLDProfPic.png);
		background-repeat: no-repeat;
		background-attachment: fixed;
		background-position: center;
		background-size: 100%;
		grid-column: 1 / -1;
		grid-row: 1 / -1;

		img {
			width: 100%;
			padding: 15%;
		}
	}

	.banner-text {
		font-size: 7vw;
		font-weight: bold;
		font-family: $secondaryFont;
		letter-spacing: 3px;
		grid-column: 2 / span 2;
		grid-row: 2 /* no need to type one*/;
		align-self: center;
		justify-self: center;
		z-index: 3;
	}

	.banner-face {
		grid-row: 2;
		grid-column: 4;
		border: 1px dashed $highlight;
		border-radius: 50%;
		width: 100%;
		height: 85%;

		img {
			width: 100%;
			height: 100%;
			opacity: 1;
			border-radius: 50%;
		}
	}

	.banner-phrase {
		font-size: 3vw;
		font-weight: bold;
		font-family: $mainFont;
		letter-spacing: 3px;
		grid-column: 2 / span 2;
		grid-row: 3 /* no need to type one*/;
		align-self: center;
		justify-self: center;
		z-index: 3;
	}

	.coaButt2 {
		display: grid;
		align-self: center;
		justify-self: center;
		grid-column: 4;
		grid-row: 4;
		margin: 10%;
		width: fit-content;
		border-radius: 10px;
		z-index: 3;
		padding: 2%;
		background-color: rgba(10, 3, 14, 0.166);

		svg {
			width: 20px;
			height: 20px;
		}
	}

	.follow {
		display: flex;
		justify-content: center;
		align-items: center;
		font-family: $secondaryFont;

		a {
			position: relative;
			padding: 10px 30px;
			margin: 6% 0;
			color: $textMain;
			text-decoration: none;
			text-transform: uppercase;
			letter-spacing: 2px;
			font-size: 3vw;
			overflow: hidden;
			transition: 0.3s;
			-webkit-box-reflect: below 1px linear-gradient(transparent, #0003);
		}

		a:nth-child(1) {
			filter: hue-rotate(215deg);
		}

		a:nth-child(3) {
			filter: hue-rotate(270deg);
		}

		a:hover {
			background: #e42886;
			color: rgb(201, 201, 201);
			box-shadow: 0 0 50px #e42886;
			transition-delay: 0.2s;
		}

		a:before {
			content: '';
			position: absolute;
			top: 0;
			left: 0;
			width: 10px;
			height: 10px;
			border-top: 2px solid rgb(17, 17, 1);
			border-left: 2px solid rgb(17, 17, 1);
			transition: 0.3s;
			transition-delay: 0.2s;
		}

		a:hover:before {
			width: 100%;
			height: 100%;
			border-top: 2px solid rgb(201, 201, 201);
			border-left: 2px solid rgb(201, 201, 201);
			transition-delay: 0;
		}

		a:after {
			content: '';
			position: absolute;
			bottom: 0;
			right: 0;
			width: 10px;
			height: 10px;
			border-bottom: 2px solid rgb(17, 17, 1);
			border-right: 2px solid rgb(17, 17, 1);
			transition: 0.3s;
			transition-delay: 0;
		}

		a:hover:after {
			width: 100%;
			height: 100%;
			border-bottom: 2px solid rgb(201, 201, 201);
			border-right: 2px solid rgb(201, 201, 201);
			transition-delay: 0.2s;
		}
	}

	//completed work block

	.comp_block,
	.comp_block_l {
		margin: $full_marg;
	}

	.comp_block,
	.comp_block_l {
		.comp_body,
		.comp_body_l {
			a {
				margin: $full_marg;
			}
		}
	}
	//tech stack div

	.tekStack {
		position: absolute;
		width: 50%;
		height: 50%;
		display: grid;
		grid-template-columns: 1fr 1fr 1fr;
		gap: $marg;
		align-items: center;
		right: 0;
		margin: -10% 10% 10% 0;

		p {
			margin: 0;
			position: relative;

			svg {
				max-width: 80%;
				background-color: $textMain;
				border-radius: 8px;
				padding: $pad;
			}
		}
	}

	@media (max-width: 768px) {
		.art-banner {
			grid-template-columns: 20px 1fr 1fr 1fr 60px;
			grid-template-rows: 20px 1fr 1fr 1fr 70px;
		}

		.butt {
			position: relative;
			margin-left: 25vw;
		}

		.coaButt2 {
			grid-column: 1;
			grid-row: 4;
			width: 10vw;

			a {
				margin: 2%;
			}
		}
	}

	@media only screen and (min-width: 780px) {
		.banner-face {
			grid-row: 2;
			grid-column: 4;
			width: 50%;
			height: 80%;

			img {
				width: 100%;
				height: 100%;
			}
		}

		.coaButt2 {
			svg {
				width: 25px;
				height: 25px;
			}
		}

		.social {
			width: 50%;
			height: 50%;
			margin: 5% 0 0 10%;
		}

		//tek_stack

		.tekStack {
			position: absolute;
			width: 40%;
			height: 40%;
			right: 0;
			margin: -10% 10% 0 0;

			svg {
				max-width: 8%;
				position: relative;
				margin-bottom: 15%;
			}
		}
	}
</style>
