<style lang="scss">

    @import 'components';

    body{
        overflow-x: hidden;
    }
    

    .social{
        font-size: 1.5vw;
    img{
        margin: -8% 10% 0 10%;
    }
    }
//entry banner

.art-banner {
    height: 100vh;
    display: grid;
    background:$backgroundMain;
    grid-template-columns: 20px 1fr 1fr 1fr 20px;
    grid-template-rows: 20px 1fr 1fr 1fr 20px;
    margin: 5px 0;
    z-index: 99;

    a {
        font-size: 3vw;
    }

}

.art-banner-bg {
    background-image: url(/img/biglogo.png);
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;
    background-size: 80%;
    opacity: 0.25;
    grid-column: 1 / -1;
    grid-row: 1 / -1;

    img {
        width: 100%;
        padding: 15%;
    }
}

.banner-text {
    font-size: 7vw;
    font-weight: bold;
    font-family: $secondaryFont;
    letter-spacing: 3px;
    grid-column: 2 / span 2;
    grid-row: 2 /* no need to type one*/;
    align-self: center;
    justify-self: center;
    z-index: 3;
}



.coaButt2 {
    display: grid;
    align-self: center;
    justify-self: center;
    grid-column: 3;
    grid-row: 4;
    margin:5% 0 5% 30%;
    width: fit-content;
    border-radius:10px;
    z-index: 3;
    padding: 4%;
    background-color: rgba(10, 3, 14, 0.363);
}


   .follow{ 

         display: flex;
         justify-content: center;
         align-items: center;
         font-family: $secondaryFont;
         

        a{
            position: relative;
            padding: 10px 30px;
            margin: 6% 0;
            color: $textColorMain;
            text-decoration: none;
            text-transform: uppercase;
            letter-spacing: 2px;
            font-size: 3vw;
            overflow: hidden;
            transition: 0.3s;
            -webkit-box-reflect: below 1px linear-gradient(transparent,#0003);
        }

        a:nth-child(1) {
            filter: hue-rotate(215deg);
        }

        a:nth-child(3) {
            filter: hue-rotate(270deg);
        }

        a:hover {
            background: #e42886;
            color:rgb(201, 201, 201) ;
            box-shadow: 0 0 50px #e42886;
            transition-delay: 0.2s;
        }

        a:before {
            content: '';
            position: absolute;
            top:0;
            left: 0;
            width: 10px;
            height: 10px;
            border-top: 2px solid rgb(17, 17, 1);
            border-left: 2px solid rgb(17, 17, 1);
            transition: 0.3s;
            transition-delay: 0.2s;
        }

        a:hover:before {
            width: 100%;
            height: 100%;
            border-top: 2px solid rgb(201, 201, 201) ;
            border-left: 2px solid rgb(201, 201, 201) ;
            transition-delay: 0;
        }

        a:after {
            content: '';
            position: absolute;
            bottom:0;
            right: 0;
            width: 10px;
            height: 10px;
            border-bottom: 2px solid rgb(17, 17, 1);
            border-right: 2px solid rgb(17, 17, 1);
            transition: 0.3s;
            transition-delay: 0;
        }

        a:hover:after {
            width: 100%;
            height: 100%;
            border-bottom: 2px solid rgb(201, 201, 201) ;
            border-right: 2px solid rgb(201, 201, 201) ;
            transition-delay: 0.2s;
        }
}

//tech stack div

.tekStack{

    margin:0 11%;

    svg{
        max-width:15%;
        margin: 3%;

        background-color:$textColorMain;
        padding:2%;
        border-radius: 8px;
    }
}






 @media (max-width: 768px) {
          
        .art-banner{
            grid-template-columns: 20px 1fr 1fr 1fr 60px;
            grid-template-rows: 20px 1fr 1fr 1fr 70px;
        }

        .coaButt2 {
            grid-column: 5;
            grid-row: 4;
            width: 10vw;

            a{
                padding: 2%;
            }

            .tekStack{

                svg{
            max-width:10%;
    }
}
        }


      }

       @media (max-width: 991px) {
          
        

      }




</style>